package com.orgs;

public class Incremental {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10;
int b=a++;
System.out.println(a);
System.out.println(b);
a++;
System.out.println(a);
b++;
System.out.println(b);

int h=20;
int j=h--;
System.out.println(h);
System.out.println(j);

	}

}

